<?phpinclude_once 'db_connect.php';session_start();$count = $_POST['itemCount'];$name = $_POST['item_name_1'];$i = 1;$j = 0;$query = $bdd->prepare("INSERT INTO commande (montant) VALUES (0)");$query->execute();$id_commande = $bdd->lastInsertId();$prix_commande = 0;while ($j != $count)	{	$j++;	$qte = $_POST['item_quantity_' . $i];	$price = $_POST['item_price_' . $i];	$name = $_POST['item_name_' . $i];	$prixtotal = $qte * $price;	$prix_commande = $prix_commande + $prixtotal;	$query1 = $bdd->prepare("INSERT INTO ligne_commande (id_commande, libelle, montant_unitaire, montant_total, quantite) VALUES (:id_commande, :name, :price, :prixtotal, :qte )");	$query1->bindValue(':id_commande', $id_commande);	$query1->bindValue(':name', $name);	$query1->bindValue(':price', $price);	$query1->bindValue(':prixtotal', $prixtotal);	$query1->bindValue(':qte', $qte);	$query1->execute();	$i++;	}        	$query2 = $bdd->prepare("UPDATE commande SET montant= :prix_commande, id_membres = :id_membres WHERE id_commande= :id_commande");	$query2->bindValue(':id_commande', $id_commande);        $query2->bindValue(':id_membres', $_SESSION['user']->getId());	$query2->bindValue(':prix_commande', $prix_commande); 	$query2->execute();	header('Location: ../index.php');